mapper.select:
select
  id, url, path, component, name, iconCls, keepAlive, requireAuth, parentId, enabled
from menu


        and id = #{id}


        and url = #{url}


        and path = #{path}


        and component = #{component}


        and name = #{name}


        and iconCls = #{iconcls}


        and keepAlive = #{keepalive}


        and requireAuth = #{requireauth}


        and parentId = #{parentid}


        and enabled = #{enabled}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from menu


        and id = #{id}


        and url = #{url}


        and path = #{path}


        and component = #{component}


        and name = #{name}


        and iconCls = #{iconcls}


        and keepAlive = #{keepalive}


        and requireAuth = #{requireauth}


        and parentId = #{parentid}


        and enabled = #{enabled}
